package com.dcy.kandg;

import javax.crypto.Cipher;
import javax.crypto.spec.IvParameterSpec;
import javax.crypto.spec.SecretKeySpec;
import org.apache.commons.codec.binary.Hex;

public class DecryptLyrics {

	public static void main(String[] args) throws Exception {
		String enctext = "";
		byte[] output = null;

		byte[] kv = { (byte) 0x06, (byte) 0xa9, (byte) 0x21, (byte) 0x40,
				(byte) 0x36, (byte) 0xb8, (byte) 0xa1, (byte) 0x5b,
				(byte) 0x51, (byte) 0x2e, (byte) 0x03, (byte) 0xd5,
				(byte) 0x34, (byte) 0x12, (byte) 0x00, (byte) 0x06 };

		byte[] iv = { (byte) 0x3d, (byte) 0xaf, (byte) 0xba, (byte) 0x42,
				(byte) 0x9d, (byte) 0x9e, (byte) 0xb4, (byte) 0x30,
				(byte) 0xb4, (byte) 0x22, (byte) 0xda, (byte) 0x80,
				(byte) 0x2c, (byte) 0x9f, (byte) 0xac, (byte) 0x41 };

		SecretKeySpec skey = new SecretKeySpec(kv, "AES");
		IvParameterSpec ips = new IvParameterSpec(iv);
		Cipher cipher = Cipher.getInstance("AES/CBC/PKCS5Padding");
		cipher.init(Cipher.DECRYPT_MODE, skey, ips);

		output = cipher.doFinal(Hex.decodeHex(enctext.toCharArray()));
		System.out.println(new String(output));
	}
}